#! /usr/bin/env python3

from labmath import semiprimegen    # Available via pip (https://pypi.org/project/labmath/)
from time import time

A106125, A357741 = [], [] # Numbers n such that the nth semiprime ==  0 (mod n), and the corresponding semiprimes
A106126, A106126sp = [], [] # Numbers n such that the nth semiprime ==  1 (mod n), and the corresponding semiprimes
A106127, A106127sp = [], [] # Numbers n such that the nth semiprime ==  2 (mod n), and the corresponding semiprimes
A106128, A106128sp = [], [] # Numbers n such that the nth semiprime ==  3 (mod n), and the corresponding semiprimes
A106129, A106129sp = [], [] # Numbers n such that the nth semiprime ==  4 (mod n), and the corresponding semiprimes
A106130, A106130sp = [], [] # Numbers n such that the nth semiprime ==  5 (mod n), and the corresponding semiprimes
A106131, A106131sp = [], [] # Numbers n such that the nth semiprime ==  6 (mod n), and the corresponding semiprimes
A106132, A106132sp = [], [] # Numbers n such that the nth semiprime ==  7 (mod n), and the corresponding semiprimes
A106133, A106133sp = [], [] # Numbers n such that the nth semiprime ==  8 (mod n), and the corresponding semiprimes
A106134, A106134sp = [], [] # Numbers n such that the nth semiprime ==  9 (mod n), and the corresponding semiprimes
